import React, { useState, useContext, useEffect } from 'react';
import { GlobalContext } from "../context/GlobalState";
import { Link, useHistory } from "react-router-dom";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button
} from "reactstrap";

export const EditUser = (props) => {
  const { editUser, users } = useContext(GlobalContext);
  const [selectedUser, setSelectedUser] = useState({
    id: '',
    name: '',
    phone: '',
    email: ''
  })
  const history = useHistory();
  const currentUserId = props.match.params.id;

  useEffect(() => {
    const userId = currentUserId;
    const selectedUser = users.find(user => user.id === userId);
    setSelectedUser(selectedUser);
  }, [currentUserId, users])

  const onSubmit = (e) => {
    e.preventDefault();
    editUser(selectedUser);
    history.push("/")
  }

  return (
    <Form onSubmit={onSubmit}>
      <FormGroup>
        <Label>Мэдээлэл өөрчлөх</Label>
        <Input type="text" value={selectedUser.name} onChange={e => setSelectedUser({ ...selectedUser, [e.target.name]: e.target.value })} name="name" placeholder="Нэр" required></Input>
        <Input type="text" value={selectedUser.email} onChange={e => setSelectedUser({ ...selectedUser, [e.target.email]: e.target.value })} name="email" placeholder="Имэйл" required></Input>
        <Input type="text" value={selectedUser.phone} onChange={e => setSelectedUser({ ...selectedUser, [e.target.phone]: e.target.value })} name="phone" placeholder="Утас" required></Input>
      </FormGroup>
      <Button type="submit">Солих</Button>
      <Link to="/" className="btn btn-danger ml-2">Буцах</Link>
    </Form>
  )
}
